        encChar = (0x7F&(encChar<<LShift))|(encChar>>(7-LShift));     // Left shift with carry for 7 bits
        carryChar = encChar & (0xFF >> (7 - (i % 6)));                //0..5 -> 7..2, get carry bit
        encChar = (encChar2 << (6-(i%6))) | (encChar >> ((i%6)+1));
        OutText += String.fromCharCode(encCharS2(encChar));
        if ((i % 6) === 5) {
            OutText += String.fromCharCode(encCharS2(carryChar));
            carryChar = 0x00;
        }
        encChar2 = carryChar;    
    }
    return String.fromCharCode(encCharS2(cKey >> 1))+String.fromCharCode(encCharS2((cKey & 0x01) | (LShift << 3)))+OutText;
}

function encCharS2 (Asciitxt) {                  				// encode ASCii 0..63 to readable text
    if (Asciitxt <= 25) {                                 // 0..25
        return (Asciitxt + 65);                             // A..Z (65..90)
    } else if ((Asciitxt >= 26) && (Asciitxt <= 51)) {    // 26..51
        return (Asciitxt + 71);                             // a..z (97..122)
    } else if ((Asciitxt >= 52) && (Asciitxt <= 61)) {    // 52..61
        return (Asciitxt - 4);                              // 0..9 (48..57)
    } else if (Asciitxt == 62) {
        return 46;
    } else if (Asciitxt == 63) {
        return 95;
    } else {
        return 126;
    }
}

msg.payload = DeCipher(msg.payload);
