function DeCipher (txt) {
    var cKey = decCharS2(txt.charCodeAt(0));
    var RShift = decCharS2(txt.charCodeAt(1));
    var decChar, decChar2, decChar3, j;
    var OutText = "";

    cKey = (cKey << 1) | (RShift & 0x01);
    RShift = RShift >> 3;  
    txt = txt.substring(2);                                        // remove header
    txt += String.fromCharCode(0);                                 // add padding  
    decChar2 = decCharS2(txt.charCodeAt(0));                       // get ASCii value 0..63
    j = 0;
    for (i = 1; i < txt.length; i++) {
        j++;
        decChar = decCharS2(txt.charCodeAt(i));                      // get ASCii value 0..63
        if ((j % 6)===0) {
            decChar2 = (decChar2 << 6) | decChar;      
            decChar3 = (decChar2>>RShift)|(0x7F & (decChar2<<(7-RShift)));  // Right shift with carry for 7 bits
            decChar3 = (decChar3 ^ cKey);                                   // XOR with cKey
            OutText += String.fromCharCode(decChar3);
            i++;
            decChar2 = decCharS2(txt.charCodeAt(i));                   // get ASCii value 0..63
        } else {
            decChar2 = (decChar2 << (j%6)) | (decChar >> (6-(j%6)));      
            decChar3 = (decChar2>>RShift)|(0x7F & (decChar2<<(7-RShift)));  // Right shift with carry for 7 bits
            decChar3 = (decChar3 ^ cKey);                                   // XOR with cKey
            OutText += String.fromCharCode(decChar3);
            decChar2 = decChar & (0x3F >> (j%6));
        }
    }
    return OutText.substring(0, OutText.length - 1);
}

function decCharS2 (Asciitxt) {                  				// decode readble text to ASCii 0..127
    if ((Asciitxt >= 65) && (Asciitxt <= 90)) {           // A..Z (65..90)
        return (Asciitxt - 65);                             // 0..25
    } else if ((Asciitxt >= 97) && (Asciitxt <= 122)) {   // a..z (97..122)
        return (Asciitxt - 71);                             // 26..51
    } else if ((Asciitxt >= 48) && (Asciitxt <= 57)) {    // 0..9 (38..57)
        return (Asciitxt + 4);                              // 52..61
    } else if (Asciitxt == 46) {
        return 62;
    } else if (Asciitxt == 95) {
        return 63;
    } else {
        return 126;
    }
}

