function HidxChange (nowHidxT, prevHidxT) {
  if (prevHidxT != nowHidxT) return nowHidxT;
  else return "no";
}

function rainStatus (nowRainMM, prevRainMM) {
  var IsRain = "no";
  if (nowRainMM > 0) {    // raining
    if (prevRainMM > 0) IsRain = "raining";
    else IsRain = "start";
  } else {                            // no rain
    if (prevRainMM > 0) IsRain = "stop";
    else IsRain = "no";
  }
  return IsRain;
}

function calcHeatIndex (tempC, RH) {   // calc Heat Index
  var T = (tempC*9/5)+32;
  var Hc1 = -42.379+(2.04901523*T)+(10.14333127*RH)-(0.22475541*T*RH)-(0.00683783*T*T)-(0.05481717*RH*RH)+(0.00122874*T*T*RH)+(0.00085282*T*RH*RH)-(0.00000199*T*T*RH*RH)
  var Hc2 = ((T>=80)&&(T<=112)&&(RH<13))?(-1*((13-RH)/4)*Math.sqrt((17-Math.abs(T-95))/17)): (((T>=80)&&(T<=87)&&(RH>85))?((RH-85)/10)*((87-T)/5): 0);
  var HidxF = ((Hc1+Hc2)<80)?0.5*(T+61+((T-68)*1.2)+(RH*0.094)): (Hc1+Hc2);
  var HidxC = Math.round((HidxF-32)*5/9);
  var HidxT = (HidxC>50)?"extreme hot":((HidxC>40)?"very hot":((HidxC>35)?"hot":"comfort"));
  var objHidx = { 'HidxC': HidxC, 'HidxText': HidxT };
  return objHidx;
}

function ResponseMSG (msg, respCode, respInfo) {
  var respMSG = {
    'proto': msg.remote.proto,
    '_session': msg.remote._session,
    'ip': msg.remote.ip,
    'port': msg.remote.port,
    'payload': respCode,
    'info': respInfo
  }
  return respMSG;
}

function roundToTwo(num) { return +(Math.round(num + "e+2")  + "e-2"); }

function seqStep (prevSEQ, thisSEQ) {
  var prev = parseInt ("0x" + prevSEQ);
  var next = parseInt ("0x" + thisSEQ);
  if (thisSEQ < prevSEQ) {        // roll over
    return (65536 - prev) + next;
  } else {
    return next - prev;
  }  
}

